import matplotlib.pyplot as plt

def task_func(data):
    if not data:
        return None
    df = pd.DataFrame(data)
    plt.figure()
    for column in df.columns:
        if column == 'Time':
            continue
        plt.plot(df['Time'], df[column], label=column)
    plt.xlabel('Time')
    plt.ylabel('Data Points')
    plt.title('Data over Time')
    plt.legend()
    return plt.gca()

