import json
import re
from collections import Counter

REPLACE_NONE = "None"

def task_func(json_str):
    data = json.loads(json_str)

    def clean_data(item):
        if isinstance(item, dict):
            return {k: clean_data(v) for k, v in item.items() if v is not None}
        elif isinstance(item, list):
            return [clean_data(v) for v in item if v is not None]
        elif isinstance(item, str) and re.match(r"[^@]+@[^@]+\.[^@]+", item):
            return REPLACE_NONE
        else:
            return item

    processed_data = clean_data(data)

    def extract_values(obj):
        if isinstance(obj, dict):
            for v in obj.values():
                yield from extract_values(v)
        elif isinstance(obj, list):
            for item in obj:
                yield from extract_values(item)
        else:
            yield obj

    values = list(extract_values(processed_data))
    value_counts = Counter(values)

    return {"data": processed_data, "value_counts": value_counts}

