import os
import shutil

def task_func(src_dir, dest_dir, extension):
    files_moved = 0

    for file_name in os.listdir(src_dir):
        if file_name.endswith(extension):
            shutil.move(os.path.join(src_dir, file_name), os.path.join(dest_dir, file_name))
            files_moved += 1

    return files_moved

import os
import shutil

def task_func(filename, dest_dir):
    try:
        os.makedirs(dest_dir, exist_ok=True)
    except OSError as e:
        if e.errno != errno.EEXIST:
            raise

    dest = shutil.copy(filename, dest_dir)

    with open(filename, 'w') as original_file:
        original_file.truncate(0)

    return os.path.abspath(dest)

