import os
import re
import shutil

def task_func(source_directory: str, target_directory: str, pattern: str = r"\d{4}") -> int:
    moved_files_count = 0

    # Check if source directory exists and is a directory
    if not os.path.exists(source_directory) or not os.path.isdir(source_directory):
        return 0

    # Create target directory if it doesn't exist
    if not os.path.exists(target_directory):
        os.makedirs(target_directory)

    # Walk through the source directory to find files
    for root, _, files in os.walk(source_directory):
        for file in files:
            if re.search(pattern, file):
                # Move the file from source to target
                shutil.move(
                    os.path.join(root, file),
                    os.path.join(target_directory, file)
                )
                moved_files_count += 1

    return moved_files_count

