import random
import matplotlib.pyplot as plt

# Constants
SALARY_RANGE = (20000, 100000)

def task_func(dict1):
    emp_salaries = []

    for prefix, num_employees in dict1.items():
        if prefix.strip().upper() == 'EMPXX':
            for _ in range(num_employees):
                salary = random.randint(*SALARY_RANGE)
                emp_salaries.append(salary)

    plt.hist(emp_salaries, bins=12, alpha=0.5)
    plt.title('Salary Distribution in EMPXX Department')
    plt.xlabel('Salary')
    plt.ylabel('Number of Employees')
    return plt.gca()

import pandas as pd
import numpy as np
DEFAULT_COLUMNS = ['Element', 'Count']
def task_func(elements, include_index=False):
    elements_series = pd.Series(elements)
    count_series = elements_series.apply(lambda x: len(x))
    if include_index:
        data_dict = {
            'Index': np.arange(len(elements)),
            'Element': elements_series,
            'Count': count_series
        }
    else:
        data_dict = {
            'Element': elements_series,
            'Count': count_series
        }
    count_df = pd.DataFrame(data_dict)
    return count_df

