import random
import string
from matplotlib import pyplot as plt

def task_func(elements, seed=100):
    random.seed(seed)
    random_patterns = []

    for element in elements:
        random_str = ''.join(random.choices(string.ascii_letters + string.digits, k=5))
        pattern = '% {}%'.format(random_str)
        random_patterns.append(pattern)

    char_count = {}
    for pattern in random_patterns:
        for char in pattern:
            if char in char_count:
                char_count[char] += 1
            else:
                char_count[char] = 1

    _, ax = plt.subplots()
    ax.bar(char_count.keys(), char_count.values())

    return random_patterns, ax, char_count

import subprocess
import os
import signal
import time

def task_func(process_name: str) -> int:
    try:
        pids = subprocess.check_output(['pgrep', '-f', process_name]).decode().split('\n')[:-1]
    except subprocess.CalledProcessError:
        pids = []

    for pid in pids:
        os.kill(int(pid), signal.SIGTERM)

    time.sleep(1)

    return len(pids)

