import string

INVALID_CHARACTERS = string.punctuation + string.whitespace

def task_func(directory_path):
    summary = {}
    for filename in os.listdir(directory_path):
        filepath = os.path.join(directory_path, filename)
        if not os.path.isfile(filepath):
            continue  # Skip directories

        if any(char in INVALID_CHARACTERS for char in filename):
            dest_dir = 'Invalid'
        else:
            extension = os.path.splitext(filename)[-1].strip('.')
            dest_dir = 'NoExtension' if not extension else extension

        dest_path = os.path.join(directory_path, dest_dir)
        if not os.path.exists(dest_path):
            os.mkdir(dest_path)

        shutil.move(filepath, dest_path)
        summary[dest_dir] = summary.get(dest_dir, 0) + 1

    return summary

