import os
import re

def task_func(pattern: str, replacement: str, directory: str) -> bool:
    try:
        files = os.listdir(directory)
        matching_files = []
        new_filenames = []

        for file in files:
            if re.search(pattern, file):
                new = re.sub(pattern, replacement, file)
                if new != file:
                    matching_files.append(file)
                    new_filenames.append(new)

        # Check for existing files with new names
        existing_files = set(files)
        for new in new_filenames:
            if new in existing_files:
                return False

        # Check for duplicate new filenames
        if len(new_filenames) != len(set(new_filenames)):
            return False

        # Proceed with renaming
        for old, new in zip(matching_files, new_filenames):
            os.rename(os.path.join(directory, old), os.path.join(directory, new))

        return True
    except Exception:
        return False

