import base64
import hashlib
import os

def task_func(password, SALT_LENGTH=32):
    if not password:
        raise ValueError("The password provided is empty or None.")
    salt = os.urandom(SALT_LENGTH)
    password_bytes = password.encode('utf-8')
    hashed_password = hashlib.pbkdf2_hmac('sha256', password_bytes, salt, 100000)
    return base64.b64encode(salt), base64.b64encode(hashed_password)

