import subprocess
import os
import signal
import time

def task_func(process_name: str) -> int:
    try:
        pids = subprocess.check_output(['pgrep', '-f', process_name]).decode().split()
    except subprocess.CalledProcessError:
        pids = []

    for pid in pids:
        try:
            os.kill(int(pid), signal.SIGTERM)
        except OSError:
            pass

    time.sleep(1)

    return len(pids)

