import numpy as np
import matplotlib.pyplot as plt
import cmath

def task_func(x, y):
    if not (isinstance(x, np.ndarray) and isinstance(y, np.ndarray)):
        raise TypeError("x and y must be numpy arrays")

    if x.size == 0 or y.size == 0:
        raise ValueError("x and y must be non-empty arrays")

    if len(x) != len(y):
        raise ValueError("Mismatched array sizes: x and y must have the same length")

    Z = np.zeros((len(y), len(x)), dtype=float)
    for i in range(len(y)):
        for j in range(len(x)):
            z = complex(x[j], y[i])
            Z[i, j] = cmath.phase(z**2 - 1)

    fig, ax = plt.subplots()
    c = ax.imshow(Z, extent=(np.amin(x), np.amax(x), np.amin(y), np.amax(y)), origin='lower', cmap='hsv')
    fig.colorbar(c, ax=ax, label="Phase (radians)")
    ax.grid()

    return ax, Z

import pandas as pd
from sklearn.preprocessing import StandardScaler

def task_func(data):
    dataframe = pd.DataFrame(data).copy()

    scaler = StandardScaler()

    for column in dataframe.columns:
        if dataframe[column].dtype in ["float64", "int64"]:
            dataframe[column] = scaler.fit_transform(
                dataframe[column].values.reshape(-1, 1)
            )
        else:
            converted = pd.to_numeric(dataframe[column], errors='coerce')
            if converted.hasnans:
                continue
            else:
                dataframe[column] = scaler.fit_transform(converted.values.reshape(-1, 1))
    return dataframe

