import math

def task_func(s):
    numbers = []
    for part in s.split(','):
        part = part.strip()
        if part:
            try:
                num = float(part)
                numbers.append(num)
            except ValueError:
                pass
    count = len(numbers)
    sqrt_sum = sum(math.sqrt(num) for num in numbers)
    return count, sqrt_sum

