import os
import hashlib
import base64

def task_func(password, PREFIX="ME", SALT_LENGTH=16):
    if SALT_LENGTH < 0:
        raise ValueError("Salt length cannot be negative")

    salt = os.urandom(SALT_LENGTH).hex()
    salted_password = PREFIX + password + salt

    hashed_password = hashlib.sha256(salted_password.encode()).digest()

    return base64.b64encode(hashed_password).decode()

