STOPWORDS = ["Those", "are", "the", "words", "to", "ignore"]
def task_func(text):
    sentences = re.split(r"\.\s*", text)
    sentence_counts = {}
    for i, sentence in enumerate(sentences):
        if sentence.strip() == "":
