import subprocess
import os
import signal
import time

def task_func(process_name: str) -> int:
    try:
        initial_pids = subprocess.check_output(['pgrep', '-x', process_name]).decode().split()
    except subprocess.CalledProcessError:
        initial_pids = []

    for pid in initial_pids:
        os.kill(int(pid), signal.SIGTERM)

    time.sleep(1)

    try:
        new_pids = subprocess.check_output(['pgrep', '-x', process_name]).decode().split()
    except subprocess.CalledProcessError:
        new_pids = []

    stopped = 0
    for pid in initial_pids:
        if pid not in new_pids:
            stopped += 1

    return stopped

