import binascii
import io
import gzip

def task_func(compressed_hex):
    try:
        compressed_bytes = binascii.unhexlify(compressed_hex)
        with io.BytesIO(compressed_bytes) as bio:
            with gzip.GzipFile(fileobj=bio) as gz:
                decompressed_bytes = gz.read()
        decoded_string = decompressed_bytes.decode('utf-8')
        return decoded_string
    except binascii.Error as e:
        return f"Error decoding hex: {e}"
    except gzip.BadGzipFile as e:
        return f"Error during decompression: {e}"
    except UnicodeDecodeError as e:
        return f"Error decoding bytes to UTF-8: {e}"
    except Exception as e:
        return f"Unexpected error: {e}"

import pandas as pd
import re

def task_func(data, mapping):
    df = pd.DataFrame(data)
    pattern = re.compile(r'\b[A-Z]+\b')

    def replace_match(match):
        word = match.group()
        return mapping.get(word, word)

    df = df.applymap(lambda x: re.sub(pattern, replace_match, x) if isinstance(x, str) else x)

    return df

