import os
import glob
from collections import Counter

def task_func(directory, extensions=[".txt", ".docx", ".xlsx", ".csv"], keep_zero=True):
    if not os.path.isdir(directory):
        raise OSError("directory must exist and be a directory.")

    counter = Counter()

    for suffix in extensions:
        pattern = os.path.join(directory, '**', '*' + suffix)
        count = len(glob.glob(pattern, recursive=True))
        if count:
            counter[suffix] += count
        else:
            if keep_zero:
                counter[suffix] += count
    return counter

import numpy as np
import pandas as pd
from sklearn.decomposition import PCA

def task_func(array, seed=None):
    if seed is not None:
        np.random.seed(seed)

    if not isinstance(array, np.ndarray) or len(array.shape) != 2:
        raise ValueError("Input must be a 2D numpy array.")

    if array.size == 0 or array.shape[1] == 0:
        return pd.DataFrame(columns=["PC1", "PC2"])

    shuffled_array = np.copy(array)
    if shuffled_array.shape[1] > 0:
        cols = np.random.permutation(shuffled_array.shape[1])
        shuffled_array = shuffled_array[:, cols]

    n_components = min(2, shuffled_array.shape[1])
    pca = PCA(n_components=n_components)
    principal_components = pca.fit_transform(shuffled_array)

    column_labels = ["PC1", "PC2"][:n_components]
    df = pd.DataFrame(data=principal_components, columns=column_labels)

    return df

