import pandas as pd
from scipy.spatial.distance import pdist, squareform

def task_func(array):
    if not isinstance(array, list) or not all(isinstance(sublist, list) for sublist in array):
        raise TypeError("Input must be a list of lists with numeric elements.")

    for sublist in array:
        if not all(isinstance(item, (int, float)) for item in sublist):
            raise TypeError("All elements in the sublists must be int or float, found a non-numeric element.")

    columns = [chr(65 + i) for i in range(len(array[0]))]  # Corrected from 64 to 65
    df = pd.DataFrame(array, columns=columns)

    distances = pdist(df.values, metric="euclidean")
    distance_matrix = pd.DataFrame(
        squareform(distances), index=df.index, columns=df.index
    )

    return df, distance_matrix

