import pandas as pd

def task_func(input_df):
    def clean_text_and_calculate_length(row):
        if pd.isnull(row['text']):
            return pd.Series(['', 0], index=['clean_text', 'text_length'])
        cleaned_text = re.sub('[^A-Za-z0-9]+', '', str(row['text']))
